#include "AT.h"#include "ATSystem.h"#include "ATEAGLView.h"#include "ATAppDelegate.h"#import <UIKit/UIKit.h>static int argc;static char** argv;AT_API void ATSystem_InitPlatformSpecific(ATSystemInitData* data){	argc = data->argc;	argv = data->argv;#if __IPHONE_OS_VERSION_MIN_REQUIRED < __IPHONE_3_2	[[UIApplication sharedApplication] setStatusBarHidden:YES animated:NO];#endif}NSAutoreleasePool *pool;AT_API void ATSystem_Main(){	pool = [[NSAutoreleasePool alloc] init];	UIApplicationMain(argc, argv, @"UIApplication", NSStringFromClass([ATAppDelegate class]));}AT_API void ATSystem_DeinitPlatformSpecific(){	[pool release];}AT_API void ATSystem_DisplayScreen(){	[((ATAppDelegate *)[UIApplication sharedApplication].delegate).glView DisplayScreen];}